import React from "react";

export default class Table extends React.Component {
    render() {

        const list = this.props.users.map((user, index) => {
            let date = new Date(user.dob.date);
            date = `${date.getFullYear()}-${date.getMonth()}-${date.getDay()}`;
            return (
                <tr key={user.name.first}>
                    <td>{user.name.first}</td>
                    <td>{user.name.last}</td>
                    <td>{date}</td>
                </tr>
            );
        });

        const filter = this.props.users.filter(user => {
            return (
                user.name.first.includes(this.props.queryText)
            );
        }).map(p => {
            return (
                <tr key={user.name.first}>
                    <td>{user.name.first}</td>
                    <td>{user.name.last}</td>
                    <td>{date}</td>
                </tr>
            );
        })

        if (this.props.queryText === "") {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <td>First name</td>
                                <td>Last name</td>
                                <td>DOB</td>
                            </tr>
                        </thead>
                        <tbody>
                            {list}
                        </tbody>
                    </table>
                </div>
            );
        }

        else {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <td>First name</td>
                                <td>Last name</td>
                                <td>DOB</td>
                            </tr>
                        </thead>
                        <tbody>
                            {filter}
                        </tbody>
                    </table>
                </div>
            );
        }

    }
}
