import React from "react";

export default class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            match: 1
        }
    }
    render() {

        const list = this.props.users.map((user, index) => {
            let date = new Date(user.dob.date);
            date = `${date.getFullYear()}-${date.getMonth()}-${date.getDay()}`;
            return (
                <tr key={user.name.first}>
                    <td>{user.name.first}</td>
                    <td>{user.name.last}</td>
                    <td>{date}</td>
                </tr>
            );
        });

        const filter = this.props.users.filter(user => {
            return (
                user.name.first.includes(this.props.queryText)
            );
        }).map(user => {
            let date = new Date(user.dob.date);
            date = `${date.getFullYear()}-${date.getMonth()}-${date.getDay()}`;
            return (
                <tr key={user.name.first}>
                    <td>{user.name.first}</td>
                    <td>{user.name.last}</td>
                    <td>{date}</td>
                </tr>
            );
        })

        const noMatch = filter.length

        console.log('Filter return length is  ', noMatch)

        if (this.props.queryText === "") {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <td>First name</td>
                                <td>Last name</td>
                                <td>DOB</td>
                            </tr>
                        </thead>
                        <tbody>
                            {list}
                        </tbody>
                    </table>
                </div>
            );
        }

        else if (noMatch === 0) {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <td>First name</td>
                                <td>Last name</td>
                                <td>DOB</td>
                            </tr>
                        </thead>
                        <tbody>

                        </tbody>
                    </table>
                </div>
            )
        }

        else {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <td>First name</td>
                                <td>Last name</td>
                                <td>DOB</td>
                            </tr>
                        </thead>
                        <tbody>
                            {filter}
                        </tbody>
                    </table>
                </div>
            );
        }





    }
}
